// src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "../components/Card";
import "./HomePage.css";

const apiKey = "Yy81ktcVuya1CursWRzogVGKzdfknHp6ZdTCthKVgSBDLa5dg8h6tsDEt"; // Coloque sua chave de API da Pexels aqui
const apiUrl = `https://api.pexels.com/v1/curated?per_page=3`;

const HomePage = () => {
  const [cardsData, setCardsData] = useState([]);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: {
            Authorization: apiKey,
          },
        });
        const imagesData = response.data.photos;
        const cards = imagesData.map((photo) => ({
          id: photo.id,
          title: `Card ${photo.id}`,
          description: "Descrição do Card",
          imageUrl: photo.src.medium,
        }));
        setCardsData(cards);
      } catch (error) {
        console.error("Error fetching images from Pexels:", error);
      }
    };

    fetchImages();
  }, []);

  return (
    <div className="home-container">
      <h2 className="home-title">Cards Integrado com Imagens da Pexels API</h2>
      <div className="cards-container">
        {cardsData.map((card) => (
          <Card
            key={card.id}
            title={card.title}
            description={card.description}
            imageUrl={card.imageUrl}
          />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
