import React, { useEffect, useState } from "react";
import axios from "axios";
import "./HomePage.css";
import Card from "../components/Card";

const HomePage = () => {
  const [randomImage, setRandomImage] = useState("");

  // Função para buscar uma imagem aleatória de uma API pública (Unsplash)
  const fetchRandomImage = async () => {
    try {
      const response = await axios.get("https://source.unsplash.com/random");
      setRandomImage(response.request.responseURL);
    } catch (error) {
      console.error("Erro ao buscar imagem aleatória:", error);
    }
  };

  useEffect(() => {
    fetchRandomImage();
  }, []);

  return (
    <div className="home-container">
      <h2 className="home-title">Imagem Aleatória</h2>
      <div className="cards-container">
        <Card
          key="random"
          title="Random Image"
          description="Imagem aleatória"
          imageUrl={randomImage}
        />
      </div>
    </div>
  );
};

export default HomePage;
